sp_widget.client_script:
function($scope, spUtil, $window, $timeout) {
  /* widget controller */
  var c = this;
  var table = $scope.data.table;
  $scope.has_succesfully_resolved = false;
  var filter = "sys_id=" + $scope.data.sys_id;
  spUtil.recordWatch($scope, table, filter, function (response) {
    var data = response.data;
    if (data.changes.includes("state")) {
      if (data.record.state.value == '5') {
        if (!$scope.has_succesfully_resolved) {
          $scope.has_succesfully_resolved = true;
          spUtil.addInfoMessage(data.display_value + ' has successfully been resolved.');
          $timeout(function () {
            $window.location.href = '/ratelock';
          }, 2000);
        }
        return;
      }
    }
    if (data.changes.includes("comments") 
      || data.changes.includes("work_notes") 
      || data.changes.includes('priority') 
      || data.changes.includes('request_type')
      || data.changes.includes('caller_id')
      || data.changes.includes('state')
      || data.changes.includes('watch_list')
      || data.changes.includes('loan_number')) {
      $scope.server.refresh();
    }
  });
}

