sp_widget.client_script:
function ($scope, $rootScope, spUtil, $timeout, $window) {
  /* widget controller */
  var c = this;
  $scope.showSession = false;
  $scope.table = $scope.data.table;
  $scope.num_pages = $scope.data.num_pages;
  $scope.tickets = $scope.data.tickets;
  c.$onInit = function() {
    spUtil.recordWatch($scope, $scope.table, $scope.data.filter, function(response) {
      console.log(response)
    });
  }
  c.$onDestroy = function() {
    $timeout.cancel();
  };

  $window.addEventListener('beforeunload', function() {
    $timeout.cancel();
  })
  $rootScope.$on('ratelock.quick-filter', function (evt, query) {
    $scope.data.queryParams = query;
		$scope.data.page = 1;
    $scope.server.update().then(function (results) {
      $scope.tickets = results.tickets;
    });
  });

  $scope.onPageChange = function () {
    $scope.listIsLoading = true;
    $scope.next_text = '<i class="fas fa-spinner fa-spin"></i>';
    $scope.server.update().then(function (results) {
      $scope.tickets = results.tickets;
      $scope.next_text = undefined;
      $scope.listIsLoading = false;
    });
  };

  $scope.truncate = function (text) {
    if (!text) return;
    var lines = text.split(/\n+/gm);
    var truncated = '';
    if (lines.length > 3) {
      lines = lines.slice(0, 3);
      truncated += lines.map(function (line) {
        return '<span class="text-muted">' + line + '</span>';
      }).join('<br /><br />') + '<span class="text-muted">&nbsp;...</span>';
    } else {
      truncated += lines.join('<br /><br />');
    }

    return truncated;
  }
}

