sp_widget.script:
(function () {
  /* populate the 'data' object */
  /* e.g., data.table = $sp.getValue('table'); */
  data.tickets = [];
  data.table = (input ? input.table : undefined) || options.table || $sp.getParameter("table") || "x_momo_ratelock_ticket";

  data.limit = (input ? input.limit : undefined) || options.limit || $sp.getParameter('sysparm_limit');
  data.filter = 'active=true';
  if (data.limit > 20)
    data.limit = 20;

  var gr = new GlideRecord(data.table);
  var ga = new GlideAggregate(data.table);
  ga.addAggregate('COUNT');

  if (input) {
    if (input.queryParams) {
      data.filter = input.queryParams;
      ga.addEncodedQuery(input.queryParams);
      gr.addEncodedQuery(input.queryParams);
    } else {
      ga.addQuery('active', true);
      ga.addNullQuery('resolved');
      gr.addQuery('active', true);
      gr.addNullQuery('resolved');
    }
  } else {
    ga.addQuery('active', true);
    ga.addNullQuery('resolved');
    gr.addQuery('active', true);
    gr.addNullQuery('resolved');
  }

  ga.query();
  if (ga.next()) {
    data.total = parseInt(ga.getAggregate('COUNT'));
  }

  data.total_pages = Math.max(Math.ceil(data.total / (data.limit + 1)) || 0, 1);
  var end;
  var start;
  if (input) {
    if (data.total <= data.limit) {
      end = data.total;
      data.pageTotal = data.total;
      start = 0;
    } else {
      if (input.page > 1) {
        start = input.page * data.limit;
        if (input.page == 2) {
          start = data.limit;
          end = data.limit * 2;
        } else {
          end = (input.page * data.limit) + data.limit;
        } 
        if (data.total <= end) {
          end = data.total;
        }
      } else {
        start = 0;
        end = input.page * data.limit;
      }
      data.end = end;
    }
  } else {
    start = 0;
    data.page = 1;
    end = data.limit;
    data.pageTotal = data.limit;
  }

  gr.orderBy('priority');
  gr.orderBy('number');
  gr.chooseWindow(start, end, true);
  gr.query();

  while (gr.next()) {

    if (gr) {
      data.allowSessions = gr.canRead();
    }


    var payload = {
      short_description: gr.getValue('short_description'),
      sys_id: gr.getValue('sys_id'),
      description: gr.getValue('description'),
      caller_id: gr.getDisplayValue('caller_id'),
      priority: gr.getValue("priority"),
      loan_number: gr.getValue('loan_number')
    };

    payload.widget = $sp.getWidget('ratelock_list_sessions', {
      sys_id: gr.getUniqueValue(),
      table: gr.getTableName()
    });

    data.tickets.push(payload);
  }

})();
