sp_angular_provider.script:
function ratelockDynamicSpWidget() {

  function ratelockDynamicSpWidgetLink(scope, elem, attrs) {}

  function ratelockDynamicSpWidgetCtrl($scope, $timeout) {
    var c = this;

    $scope.widgetIsChanging = false;

    c.$onInit = function () {
      setWidget();
    };

    $scope.$watch('widget', function (cur, prev) {
      if (!cur) return;

      if (cur !== prev) {
        setWidget();
      }
    });

    function setWidget() {
      $scope.widgetIsChanging = true;
      c.widget = $scope.widget;

      $timeout(function () {
        $scope.widgetIsChanging = false;
      }, 250);
    }
  }

  return {
    restrict: 'E',
    replace: true,
    scope: {
      widget: '='
    },
    template: '<span ng-if="!widgetIsChanging && c.widget"><sp-widget widget="c.widget"></sp-widget></span>',
    link: ratelockDynamicSpWidgetLink,
    controller: ratelockDynamicSpWidgetCtrl,
    controllerAs: 'c',
    bindToController: true
  };
}
