sp_angular_provider.script:
function spViewport($window) {
  return {
    restrict: 'A',
		scope: {
			onViewportChange: '&'
		},
    link: function (scope, elem, attrs) {

			scope.onViewportChange(undefined, {
				isVisible: isInViewport(elem[0])
			});

      window.addEventListener('scroll DOMContentLoaded resize', function (evt) {
        scope.onViewportChange(evt, {
          isVisible: isInViewport(elem[0])
        });
      });

      function isInViewport(element) {
        var bounding = element.getBoundingClientRect();
        var clientWidth = (window.innerWidth || document.documentElement.clientWidth);
        return (
          bounding.top >= 0 &&
          bounding.left >= 0 &&
          bounding.bottom <= clientWidth &&
          bounding.right <= clientWidth
        );
      }
    }
  };
}
