sp_widget.client_script:
function ratelockRecipientsInput($scope, $rootScope, spUtil, $timeout) {
	var c = this;
	$scope.fromWatcher = false;

	spUtil.recordWatch($scope, $scope.data.table, "sys_id=" + $scope.data.sys_id, function(response) {
		var data = response.data;
		if (data.changes.includes('email_recipients')) {
			$scope.fromWatcher = true;
			if (!$scope.alreadyRefreshed) {
				$scope.alreadyRefreshed = true;
				$scope.server.refresh().then(function(results) {
					console.log(results);
					$scope.email_recipients.displayValue = results.data.displayValue;
					$scope.email_recipients.value = results.data.value;

					$timeout(function() {
						$scope.alreadyRefreshed = false;
						$scope.fromWatcher = false;
					}, 1000);
				});
			}
		}
	});

  $scope.email_recipients = {
    displayValue: $scope.data.displayValue,
    value: $scope.data.value,
    name: 'email_recipients'
  };

	$rootScope.$on('editor.mention.added', function(evt, payload) {
		var item = payload.item;
		if (payload.id !== c.options.sys_id) return;
		if ($scope.email_recipients.value === '' || !$scope.email_recipients.value) {
			$scope.data.displayValue = item.value;
			$scope.data.value = item.id;
		} else {
			$scope.data.displayValue = $scope.data.displayValue + ',' + item.value;
			$scope.data.value = $scope.data.value + ',' + item.id;
		}
		$scope.server.update();
	});

	$scope.$on("field.change.email_recipients", function(evt, args) {
		if (args.field.name === 'email_recipients' && args.newValue !== args.oldValue) {
			$scope.data.displayValue = args.field.displayValue;
			$scope.data.value = args.field.value;
			if (!$scope.fromWatcher) {
				$scope.server.update();
			}
		}
  });
}

