sys_script.script:
(function executeRule(current, previous) {
  var messages = [];
  var CHANGE_FIELDS = [
    'state',
    'assigned_to',
    'loan_number',
    'caller_id',
    'priority',
    'request_type',
    'email_recipients'
  ];
  for (var i = 0; i < CHANGE_FIELDS.length; i++) {
    var field = CHANGE_FIELDS[i];
    if (current[field].changes()) {
      var message = '[code]<div class="history"><span class="history-label">' + current[field].getLabel() + '</span> has changed from <span class="previous-record-history">' + (previous[field].nil() ? 'Empty' : previous.getDisplayValue(field)) + '</span>' +
        ' to <span class="current-record-history">' + (current[field].nil() ? 'Empty' : current.getDisplayValue(field)) + '</span></div>' +
        '[/code]';
      messages.push(message);
    }
  }

  if (messages.length > 0) {
    current.ticket_history = messages.join('\n\n');
    current.update();
  }

})(current, previous);
