sys_script_email.script:
(function runMailScript(current, template, email, email_action, event) {
  template.print('<style type="text/css">.tdwrap{color:#ccc;line-height:1;font-size:10pt;}sup{color:#fff;}</style><!-- [if mso]><style>sup{mso-hide: all;}<span id="CmCaReT"></span></style><![endif]-->');
  template.print('<p><span style="font-size: 14pt;">Hello');
  var meta = event.parm2;
  try {
    meta = JSON.parse(meta);
  } catch (e) {}
  if (meta.type == 'technician' && !gs.nil(current.getValue('assigned_to'))) {
    template.print(' ' + current.getDisplayValue('assigned_to') + ',</span></p>');
  } else if (meta.type == 'caller' && !gs.nil(current.getValue('caller_id'))) {
    template.print(' ' + current.getDisplayValue('caller_id') + ',</span></p>');
  } else {
    template.print(',</span></p>');
  }
  template.print('<p><span style="font-size:14pt;">' + meta.header_text + '</span></p>');
})(current, template, email, email_action, event);
