sys_script_email.script:
(function(current, template, email, email_action, event){
  template.print('<style type="text/css">.tdwrap{color:#ccc;line-height:1;font-size:10pt;}sup{color:#fff;}</style><!-- [if mso]><style>sup{mso-hide: all;}<span id="CmCaReT"></span></style><![endif]-->');
  template.print('<p><span style="font-size: 14pt;">Hello');
  if (current.getValue('caller_id')) {
    template.print(' ' + current.getDisplayValue('caller_id') + ',</span></p>');
  } else {
    template.print(',</span></p>');
  }
  var updated_by = current.getValue('sys_updated_by');
  if (/\./gi.test(updated_by)) {
    updated_by = updated_by.split(".");
    updated_by = updated_by.map(function (name) {
      return name.charAt(0).toUpperCase() + name.slice(1).toLowerCase();
    });
    updated_by = updated_by.join(" ") + ' added a';
  } else if (updated_by) {
    updated_by = updated_by.charAt(0).toUpperCase() + updated_by.slice(1).toLowerCase() + ' added a';
  } else {
    updated_by = 'A ';
  }

  template.print('<p><span style="font-size:14pt;">' + updated_by + ' comment to your ratelock request.</span></p>');
})(current, template, email, email_action, event);
